import React, { useState } from 'react';
import {TouchableOpacity, View, Text, StyleSheet } from 'react-native';

export default function Datetime() {

    const date = new Date();
    var dayOfWeek = [ "日", "月", "火", "水", "木", "金", "土" ] ;
    const [year, setFullYear] = useState(date.getFullYear());
    const [month, setMonth] = useState(date.getMonth()+1);
    const [day, setDate] = useState(date.getDate());
    const [youbi, setDay] = useState(date.getDay());


    var startDate = new Date(year, month-1, 1) // 月の最初の日を取得
    var startDayCount = startDate.getDate() // 月の末日
    var endDate = new Date(year, month, 0) // 月の最後の日を取得
    var endDayCount = endDate.getDate() // 月の末日

    function onpress(month,number,youbi,flag){
        //　翌日のボタンを押した時
        if (flag == 1) {
            if (number != null) {
                //末日の場合は翌月の1日に設定する
                if (number > endDayCount) {
                number = 1;
                month = month + 1;
                }
            setDate(number);
            setMonth(month);
            //土曜日の場合は日曜日に設定
                if (youbi >= 7) {
                    youbi = 0;
                }
            setDay(youbi);
           }
        //　前日のボタンを押した時
        } else {
            if (number != null) {
                // 前の月の場合は前月の末尾を取得する
                if (number < 1) {
                endDate = new Date(year, month-1, 0) // 前月の最後の日を取得
                endDayCount = endDate.getDate() // 前月の末日
                number = endDayCount;
                month = month - 1;
                }
            setDate(number);
            setMonth(month);
                //日曜日の場合は土曜日に戻る
                if (youbi <= -1) {
                    youbi = 6;
                }
            setDay(youbi);
           }

        }
    }
    return (
        <View style={styles.appber}>
            <View style={styles.appberInner}>
                <Text style={styles.appberTitle}>Baby App</Text>
                <Text style={styles.appberRight}>ログアウト</Text>
            </View>
        </View>
    )
}

const styles = StyleSheet.create({
    appber: {
        width: '100%',
        height: 104,
        backgroundColor: '#FFDB59',
        justifyContent: 'flex-end',
    },
    appberInner: {
        alignItems: 'center',
    },
    appberRight: {
        position: 'absolute',
        right: 19,
        bottom: 16,
        color: 'rgba(103, 103, 103, 0.8)',
    },
    appberTitle: {
        marginBottom: 8,
        fontSize: 22,
        lineHeight: 32,
        color: '#737373',
        fontWeight: 'bold',
    },
});
